import { useState } from "react";
import "./App.css";

type Note = {
  id: number;
  title: string;
  content: string;
}

const App = () => {
  const [notes, setNotes] = useState<
  Note[]
  >([
    {
      id: 1,
      title: "Note Title",
      content: "Note Description"
    },
    {
      id: 2,
      title: "Note Title",
      content: "Note Description"
    },
    {
      id: 3,
      title: "Note Title",
      content: "Note Description"
    }
  ]);

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  return (
    <div className="app-container">
      <form className="note-form">
        <input
          value = {title}
          onChange={(e) => 
            setTitle(e.target.value)
          }
          placeholder="Title"
          required 
        ></input>
        <textarea
          placeholder="Content..."
          rows={10}
          required
        ></textarea>
        <button type="submit">
          Add Note
        </button>
      </form>
      <div className="notes-grid">
        {notes.map((note) => (
          <div className="note-item" key={note.id}>
            <div className="note-header">
              <button>x</button>
              <button>Edit</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
