import { useEffect, useState } from "react";
import "./App.css";

type Note = {
  id: number;
  title: string;
  content: string;
}

const App = () => {
  const [notes, setNotes] = useState<Note[]>([]);

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/notes");
        const data: Note[] = await response.json();
        setNotes(data);
      } catch (error) {
        console.error("An error occurred while fetching the notes", error);
      }
    };

    fetchNotes();
  }, []);

  const handleClickNote = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  }

  const handleAddNote = async (e: React.FormEvent) => 
    {
      e.preventDefault();
      try {
        const response = await fetch("http://localhost:5000/api/notes", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
            body: JSON.stringify({
            title: title,
            content: content
          })
        });

        const newNote = await response.json();
      } catch (e) {
        console.error("An error occurred while adding the note", e);
      }

      // Adds the new note to the notes array
      setNotes([newNote, ...notes]);
      // Clears the input fields of the form
      setTitle("");
      setContent("");
    }

    const handleEditNode = (e: React.FormEvent) => 
      {
        e.preventDefault();

        if (!selectedNote) {
          return;
        }

        // Creates a new note object
        const editedNote: Note = {
          id: selectedNote!.id,
          title: title,
          content: content
        };

        // Updates the notes array with the edited note
        setNotes(notes.map((note) => 
          note.id === editedNote.id ? editedNote : note
        ));

        setTitle("");
        setContent("");
        setSelectedNote(null);
      }

      const handleCancelEdit = () => 
        {
          setTitle("");
          setContent("");
          setSelectedNote(null);
        }

        const deleteNote = (
          event: React.MouseEvent, 
          noteId: number) =>
          {
            // Prevents the event from bubbling up the DOM tree
            event.stopPropagation();

            // Filters out the note with the id that matches the noteId
            setNotes(notes.filter((note) => 
              note.id !== noteId
            ));
          }

  return (
    <div className="app-container">
      <form className="note-form" onSubmit = {(event) => selectedNote ? handleEditNode(event) : handleAddNote(event)}>
        <input
          value = {title}
          onChange={(e) => 
            setTitle(e.target.value)
          }
          placeholder="Title"
          required 
        ></input>
        <textarea
          value={content}
          onChange = {(e) => 
            setContent(e.target.value)
          }
          placeholder="Content..."
          rows={10}
          required
        ></textarea>
        {selectedNote ? (
          <div className="edit-buttons">
            <button type="submit">
              Save
            </button>
            <button onClick = {handleCancelEdit}>
              Cancel
            </button>
          </div>
        ) : <button type="submit">
              Add Note
            </button>
        }
      </form>
      <div className="notes-grid">
        {notes.map((note) => (
          <div className="note-item" key={note.id} onClick = {() => handleClickNote(note)}>
            <div className="note-header">
              <button onClick = {(event) => 
                deleteNote(event, note.id)
                }>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
