import { useState } from "react";
import "./App.css";

const App = () => {
  const [notes, setNotes] = useState([
    {
      id: 1,
      title: "Note Title",
      content: "Note Description"
  }]);

  return (
    <div className="app-container">
      <form className="note-form">
        <input
          placeholder="Title"
          required 
        ></input>
        <textarea
          placeholder="Content..."
          rows={10}
          required
        ></textarea>
        <button type="submit">
          Add Note
        </button>
      </form>
      <div className="notes-grid">
        <div className="note-item">
          <div className="note-header">
            <button>x</button>
            <button>Edit</button>
          </div>
          <h2>Note Title</h2>
          <p>Note Description</p>
        </div>
      </div>
    </div>
  );
}

export default App;
